mapper.select:
select * from typeconnection


		AND ID = #{id,jdbcType=INTEGER}


		AND Client_ID = #{clientId,jdbcType=INTEGER}


		AND NewsType_ID = #{newstypeId,jdbcType=INTEGER}

mapper.insert:
